import React, {Component} from 'react';
import {connect} from 'react-redux'

import {
    fetchPhones,
    loadMorePhones,
    addPhoneToShop,
    removePhoneFromShop,
    fetchCategories
} from 'actions'

import {getPhones} from 'selectors'
import * as R from "ramda";
import {Link} from "react-router";

class Admin extends Component {
    constructor(props) {
        super(props);
    }

    componentDidMount () {
        this.props.fetchPhones()
        this.props.fetchCategories()
    }


    render() {
        const {phones} = this.props;
        const {addPhoneToShop} = this.props;
        console.log("phones = ", this.props);

        // return (
        //     <div>
        //         <h1>Admin's page</h1>
        //     </div>
        // )
        return (
            <div>
                <div className='books row'>
                    {phones.map((phone, index) => (
                        <tr
                            key={index}
                            className='item-checout'
                        >
                            <td className='first-column-checkout'>
                                <img
                                    className='img-thumbnail'
                                    src={phone.image}
                                    alt={phone.name}
                                />
                            </td>
                            <td>{phone.name}</td>
                            <td>${phone.price}</td>
                            <td>{phone.count}</td>
                            <td>
                                   <span
                                       className='delete-cart'
                                       onClick={() => removePhoneFromShop(phone.id)}
                                   />
                            </td>
                            <td>
                                <button onClick={() => addPhoneToShop(phone.id, phone.count)}>Добавить</button>
                            </td>
                        </tr>
                    ))}
                </div>
                <div className='row'>
                    <div className='col-md-12'>
                        <button
                            onClick={loadMorePhones}
                            className='pull-right btn btn-primary'
                        >
                            Load More
                        </button>
                    </div>
                </div>
            </div>

        )
    }


    renderPhone(phone, index) {
        const shortDescription = `${R.take(60, phone.description)}...`

        return (
            <div className='col-sm-4 col-lg-4 col-md-4 book-list' key={index}>
                <div className='thumbnail'>
                    <img
                        className='img-thumbnail'
                        src={phone.image}
                        alt={phone.name}
                    />
                    <div className='caption'>
                        <h4 className='pull-right'>${phone.price}</h4>
                        <h4>
                            <Link to={`/phones/${phone.id}`}>
                                {phone.name}
                            </Link>
                        </h4>
                        <h4 className='amount'>Amount: {phone.count}</h4>
                        <p>{shortDescription}</p>
                    </div>
                </div>
            </div>
        )
    }


}

const mapStateToProps = (state, ownProps) => {
    return {
        phones: getPhones(state)
    }
}

const mapDispatchToProps = {
    fetchPhones,
    loadMorePhones,
    addPhoneToShop,
    removePhoneFromShop,
    fetchCategories
}

export default connect(mapStateToProps, mapDispatchToProps)(Admin)